INSERT INTO linkpro.pro_service (sv_id, sv_pro_id, sv_type, sv_name, sv_pro_score, sv_price, sv_contents, sv_reside_YN, sv_main_pic) VALUES (1, 1, 'web', '홈페이지 기획부터 디자인, 제작까지 도와드립니다.', 4.8, 1430000, '타업체와 차별화된 개발능력과 디자인으로 최상의 결과물을 만나보세요.\n지금까지 보지 못했던 최상의 퀄리티로 보답하겠습니다. \'홈페이지 제작, 생각보다 비싸지 않습니다.\' \n다년간의 노하우를 바탕으로 다양한 프로젝트를 수행하였습니다. \n리모플러스는 사원이 단 한명도 없는 경력자들로 구성된 웹 에이전시입니다. 체계적인 프로세스를 통하여 고객과의 소통을 통해 최고의 만족도를 드리겠습니다.', 'N', 0x424C4F422064617461);
INSERT INTO linkpro.pro_service (sv_id, sv_pro_id, sv_type, sv_name, sv_pro_score, sv_price, sv_contents, sv_reside_YN, sv_main_pic) VALUES (2, 2, 'web', '플랫폼 사이트를 완벽하게 만들어드리겠습니다.', 5.0, 3300000, '#현재 개발업계의 현실.\n현재 개발 업계의 현실은 다양한 양상을 보이고 있습니다.\n대량 생산 방식을 통해 속도와 효율을 추구하는 곳이 있으며, 다른 한편으로는 정부 지원을 받아 짧은 기간 내에 기술을 습득하고 개발을 시작하는 사람들도 있습니다.\n하지만, 이 과정에서 프로젝트 이후 소통이 끊기는 프리랜서들로 인한 문제도 발생하고 있습니다. 이런 다양한 경로를 통해 여러 번의 시행착오를 겪은 끝에, 고객님들이 저희를 찾아주시는 경우가 많습니다. 저희는 이러한 고객님들의 믿음에 보답하기 위해, 신뢰할 수 있는 서비스 제공을 위해 끊임없이 노력하고 있습니다.', 'N', 0x424C4F422064617461);
INSERT INTO linkpro.pro_service (sv_id, sv_pro_id, sv_type, sv_name, sv_pro_score, sv_price, sv_contents, sv_reside_YN, sv_main_pic) VALUES (3, 3, 'web', '홈페이지를 꼼꼼하고 완벽하게 제작해 드립니다.', 5.0, 3300000, '홈페이지를 만들 때 물론 저렴한 홈페이지도 있지만\n저희는 내 집 같이 오래 사용할 수 있는 홈페이지를 만들어 드릴 거에요.\n다른 전문가보다 다소 비싸다는 생각이 드실 수 있어요.\n저희는 영업팀, 기획팀, 디자인팀, 퍼블리싱팀, 개발팀, 운영관리팀을 가지고 있는 웹에이전시이고 해당 공정 과정에 최고의 전문가들이 배치되어 있답니다.\n한번 만들고 실패해서 두 번 만들게 될 내 소중한 자산!\n한 번에 원스톱으로 정확하게 만들고 평생 관리가 가능한 자산관리시스템을 이용하여 유지보수를 이용해보세요!', 'N', 0x424C4F422064617461);
INSERT INTO linkpro.pro_service (sv_id, sv_pro_id, sv_type, sv_name, sv_pro_score, sv_price, sv_contents, sv_reside_YN, sv_main_pic) VALUES (4, 4, 'web', 'UI 반응형 홈페이지로 당신의 사업을 빛내드립니다.', 5.0, 750000, '홈페이지의 가장 중요한 부분은 운영하는 사업과 맞는 기획이 주가 되어야합니다.\nPC, 모바일, 태블릿 등 모든 플랫폼에서 사이트를 볼 수 있고 UI / UX 디자인으로 마무리되어야 한다고 생각합니다.\n단순하게 사이트를 디자인하는 업체는 많을지 몰라도\n사이트를 분석하고 마케팅적인 부분을 고려하며 사이트를 제작하는 업체는 거의 없습니다.\n홈페이지 기획부터 SEO는 물론 마케팅적인 요소가 들어가야 합니다.\n다른 업체와 시작부터 출발점이 다릅니다.\n\n사업을 시작하신다면 좋은 파트너가 되어드리겠습니다.\n백마디 말보다 결과로 보여드리겠습니다.\n문의 주시면 친절하게 답변드리겠습니다.', 'Y', 0x424C4F422064617461);
INSERT INTO linkpro.pro_service (sv_id, sv_pro_id, sv_type, sv_name, sv_pro_score, sv_price, sv_contents, sv_reside_YN, sv_main_pic) VALUES (5, 5, 'web', '반응형 홈페이지 개발해드립니다.', 5.0, 2200000, '*10년 이상의 고퀄리티 개발자들이 주력을 이루는 웹사이트 개발팀\n2010년부터 모바일웹앱, 홈페이지, 쇼핑몰 등 다양한 분야의 프로젝트를 50건 이상 진행한 프리랜서 그룹입니다. 국내 대기업 등 다양한 업체의 니즈에 맞춰서 프로젝트를 진행하고 있으며, 특히 모바일앱패키징 가능한 반응형 홈페이지 개발에 특기를 가지고 있습니다.\n\n서비스 제공 절차\n- 기획 및 디자인 : 피그마 제공\n대규모 서비스 및 다양한 서비스 구축 경험이 있는 전문 기획자가 전담하여 프로젝트를 기획합니다. \n다양한 검색이 가능하면서도 직관적이고 편한 UI구성을 통해 사용자의 만족도를 높일 수 있도록 기획해드립니다.\n10년차 이상의 고급 디자이너를 중심으로 최근 트렌드 반영이 가능한 디자이너들이 참여합니다.\n\n- 퍼블리싱 : 홈페이지 URL제공\n퍼블리싱 전문 개발자를 중심으로 모바일, PC에서 모두 작동하는 유연한 레이아웃을 구성해드립니다.\n각종 애니메이션 구현, 이미지 보기, 영상 보기 등 바라시는 모든 기능들이 탑제됩니다.', 'N', 0x424C4F422064617461);
INSERT INTO linkpro.pro_service (sv_id, sv_pro_id, sv_type, sv_name, sv_pro_score, sv_price, sv_contents, sv_reside_YN, sv_main_pic) VALUES (6, 6, 'app', '아이폰 안드로이드 네이티브앱을 만들어 드립니다.', 4.9, 4400000, '[IOS, Android, 네이티브앱 제작해드립니다.]\n\n10년차 소프트웨어 개발자입니다. 전분야 다수의 어플 및 웹사이트, 소프트웨어를 제작했습니다. 모든 스타일의 앱을 만들어 드립니다. 고객에게 제공되는 앱은 아이폰, 아이패드, 안드로이드 모두 정확하게 일치합니다. 이제 귀하의 아이디어를 앱으로 !!!\n\n기본 가격 정보\n- 아이폰/안드로이드 네이티브앱 제작 및 마켓에 무료로 배포해드리며 개발된 제품에서 발생하는 오류들은 무료로 수정해드립니다.\n- 아이폰, 안드로이드 기본금액 50만원, 추가 기능에 따라 견적을 제출해드립니다.', 'N', 0x424C4F422064617461);
INSERT INTO linkpro.pro_service (sv_id, sv_pro_id, sv_type, sv_name, sv_pro_score, sv_price, sv_contents, sv_reside_YN, sv_main_pic) VALUES (7, 7, 'app', '플러터 Flutter 앱 어플 네이티브 하이브리드 제작', 0.0, 5000000, 'AOS 앱 따로? iOS앱 따로? 웹 사이트 따로?\n이제 저희 팀의 \'Flutter\'로 3가지 모두 한 번에! 시간과 비용을 3배로 줄이세요!\n기존 네이티브 앱 개발은 AOS, iOS 따로따로 개발해야되다 보니 기간이 너무 오래 걸리고, 비용이 따로따로라 너무 비싼 문제가 있었습니다.\n\n하지만, \n\n저희 팀의 개발은 구글 대세 언어 \'Flutter\'로 단 1번의 개발로 AOS/iOS/웹까지 3가지 모두 출시 가능!\n\n-기존 방식 견적: AOS 500만원 + iOS 800만원 + 웹300만원 = 1,600만원(2달)\n-저희 팀 견적: AOS + iOS + 웹 = 700만원(기간 3주) 약 2.3배 비용 절약\n\n5~10년차/30대/젋은 감각의 팀\n기획자, 디자이너, 개발자, 마케터\n*링크프로 만족도 100%\n\n빠르고! 완벽하게! 무슨 기능이든!\n소비자가 사용하고 싶은 감각적인 어플 개발', 'N', 0x424C4F422064617461);
INSERT INTO linkpro.pro_service (sv_id, sv_pro_id, sv_type, sv_name, sv_pro_score, sv_price, sv_contents, sv_reside_YN, sv_main_pic) VALUES (8, 8, 'app', '# Android #iOS 개발 전문 업체', 5.0, 7000000, '안녕하세요. 저희는 기획자 디자이너 개발자로 이루어진 스타트업입니다.\n플러터를 이용하여 크로스플랫폼 어플리케이션을 제작해드립니다.\nFlutter를 이용하면, 추가 비용없이 안드로이드와 iOS개발이 가능하여\n예창/초창 분들을 위한 MVP모델 뿐만 아니라\n빠른 시장 검증을 위한 앱을 시간, 비용적으로 효율성 있게 진행해 드립니다.\n\n스타트업계 개발 트렌드 중 가장 최신 언어를 사용하여\n위치기반, SNS, 커머스 기능 등 다양한 기능들이 구현 가능합니다.\n\n앱 제작이 끝난 후에도 책임지고 유지보수를 도와드리겠습니다^^\n* 상담 및 일정 조율을 위해 결제 전 링크프로 메세지로 먼저, 문의 부탁드립니다.', 'N', 0x424C4F422064617461);
INSERT INTO linkpro.pro_service (sv_id, sv_pro_id, sv_type, sv_name, sv_pro_score, sv_price, sv_contents, sv_reside_YN, sv_main_pic) VALUES (9, 9, 'app', '매일 매일 직원처럼 소통하면서 앱개발해드립니다.', 0.0, 5000000, '유연한 개발.\n솔직한 견적.\n플랫폼 전문 개발, 히어로가 함께 합니다.\n\nReact Native를 이용해\n안드로이드, iOS앱을 동시에 제작합니다.\n\n계약하고 개발할 때 까지 의뢰인께서 기다리는 것이 아니라 영업사원이 아닌 실제 개발에 투입되는 전담 매니저가 매 영업일 1회 이상 진행 상황을 공유하고 피드백을 주고 받습니다.\n\n피드백은 의뢰인께서 가장 연락하기 쉬운 수단을 사용하구요.\n노션을 통해서도 언제 어디서나 진행 상황을 확인할 수 있도록 공유합니다.\n\n개발 경력 6년 동안 스타트업을 대상으로 실 서비스 운영 서비스를 제작해왔습니다.\n\n단순히 개발이 아니라 비즈니스에 필요한 것 부터 함께 고민하고 우선 순위를 정해가는 CTO 또는 개발직원의 마음으로 함께 가고 싶습니다.', 'Y', 0x424C4F422064617461);
INSERT INTO linkpro.pro_service (sv_id, sv_pro_id, sv_type, sv_name, sv_pro_score, sv_price, sv_contents, sv_reside_YN, sv_main_pic) VALUES (10, 10, 'app', '앱 개발 전문 팀의 Flutter 앱개발/어플 제작', 5.0, 10000000, '"우리가 개발하는 건 단순히 하나의 앱이 아닌 당신의 꿈"\n당신의 그 소중한 꿈을 케이플이 현실로 만들어 드릴게요.', 'N', 0x424C4F422064617461);
